import React, { useState } from "react";
import { useForm } from "react-final-form-hooks";
import { Formik, Form, Field, ErrorMessage } from "formik";
import "./index.css";

export default function TodoApp() {
  return (
    <div className="todo-container">
      <h1>TODO</h1>
      <ControlledTodo />
      <hr />
      <h1>TODO (React-final-form)</h1>
      <FinalFormTodo />
      <hr />
      <h1>TODO (Formik)</h1>
      <FormikTodo />
    </div>
  );
}


function ControlledTodo() {
  const [todos, setTodos] = useState([]);
  const [input, setInput] = useState("");

  const addTodo = () => {
    if (input.length >= 5) {
      setTodos([...todos, input]);
      setInput("");
    } else {
      alert("Введіть не менше 5 символів");
    }
  };

  const removeTodo = (index) => {
    setTodos(todos.filter((_, i) => i !== index));
  };

  return (
    <div className="todo-section">
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Введіть завдання"
      />
      <button onClick={addTodo}>Додати</button>
      <div className="todos">
        {todos.map((todo, index) => (
          <div key={index} className="todo-item">
            <input type="checkbox" /> {todo}
            <button onClick={() => removeTodo(index)}>Видалити</button>
          </div>
        ))}
      </div>
    </div>
  );
}


function FinalFormTodo() {
  const [todos, setTodos] = useState([]);

  const onSubmit = (values) => {
    setTodos([...todos, values.todo]);
    form.reset();
  };

  const validate = (values) => {
    const errors = {};
    if (!values.todo || values.todo.length < 5) {
      errors.todo = "Введіть не менше 5 символів";
    }
    return errors;
  };

  const { form, handleSubmit, values, errors } = useForm({
    onSubmit,
    validate,
  });

  const removeTodo = (index) => {
    setTodos(todos.filter((_, i) => i !== index));
  };

  return (
    <div className="todo-section">
      <form onSubmit={handleSubmit}>
        <input {...form.getInputProps("todo")} placeholder="Введіть завдання" />
        <button type="submit">Додати</button>
        {errors.todo && <div className="error">{errors.todo}</div>}
      </form>
      <div className="todos">
        {todos.map((todo, index) => (
          <div key={index} className="todo-item">
            <input type="checkbox" /> {todo}
            <button onClick={() => removeTodo(index)}>Видалити</button>
          </div>
        ))}
      </div>
    </div>
  );
}

// Formik
function FormikTodo() {
  const [todos, setTodos] = useState([]);

  const validate = (values) => {
    const errors = {};
    if (!values.todo || values.todo.length < 5) {
      errors.todo = "Введіть не менше 5 символів";
    }
    return errors;
  };

  const removeTodo = (index) => {
    setTodos(todos.filter((_, i) => i !== index));
  };

  return (
    <div className="todo-section">
      <Formik
        initialValues={{ todo: "" }}
        validate={validate}
        onSubmit={(values, { resetForm }) => {
          setTodos([...todos, values.todo]);
          resetForm();
        }}
      >
        {() => (
          <Form>
            <Field name="todo" placeholder="Введіть завдання" />
            <button type="submit">Додати</button>
            <ErrorMessage name="todo" component="div" className="error" />
          </Form>
        )}
      </Formik>
      <div className="todos">
        {todos.map((todo, index) => (
          <div key={index} className="todo-item">
            <input type="checkbox" /> {todo}
            <button onClick={() => removeTodo(index)}>Видалити</button>
          </div>
        ))}
      </div>
    </div>
  );
}
